insert into learning_standards (id,ref_id,progression,levels,standard) values ('2.nbt.a.1','3B25AF48C22D4668A6085998F847B56E',206,10,'Understand that the three digits of a three-digit number represent amounts of hundreds, tens, and ones; e.g., 706 equals 7 hundreds, 0 tens, and 6 ones.');
insert into learning_standards (id,ref_id,progression,levels,standard) values ('2.nbt.a.2','EE88A59EFD4348C28C56A49E61A673A8',207,10,'Count within 1000; skip-count by 5s, 10s, and 100s.');
insert into learning_standards (id,ref_id,progression,levels,standard) values ('2.nbt.a.3','EE06B2E4211C4C8EB432A5448DA82C77',208,10,'Read and write numbers to 1000 using base-ten numerals, number names, and expanded form.');
