select (select evaluations.description from evaluations where id = evaluations_attempts.evaluations_id) as milestone, (select first_name from users where id = evaluations_attempts.user_id) as captain_firstname,  (select last_name from users where id = evaluations_attempts.user_id) as captain_lastname, (select first_name from users where id = evaluations_attempts.teammate_id) as teammate_firstname, (select last_name from users where id = evaluations_attempts.teammate_id) as teammate_lastname from evaluations_attempts join users on evaluations_attempts.user_id=users.id order by evaluations_attempts.start_time desc;
